SELECT users.name FROM users
JOIN user_courses ON user_courses.user_id = users.id;

SELECT users.name, courses.title FROM users
JOIN user_courses ON user_courses.user_id = users.id
JOIN courses ON courses.id = user_courses.course_id;

SELECT users.name, courses.title, videos.title, categories.name, courses.teacher FROM users  
JOIN user_courses ON user_courses.user_id = users.id 
JOIN courses ON courses.id = user_courses.course_id 
JOIN course_videos ON courses.id = course_videos.course_id 
JOIN videos ON videos.id = course_videos.video_id 
JOIN categories ON categories.id = courses.category_id;

