SELECT users.name, courses.description, videos.title, categories.name, courses.teacher FROM users 
JOIN user_courses ON user_courses.user_id = 1 
JOIN courses ON courses.id = user_courses.course_id 
JOIN course_videos ON courses.id = course_videos.course_id 
JOIN videos ON videos.id = course_videos.video_id 
JOIN categories ON categories.id = courses.category_id WHERE users.id = 1;

