SELECT u.name, c.description, v.title, ct.name, c.teacher FROM users u JOIN user_courses uc ON uc.user_id = 1 
JOIN courses c ON c.id = uc.course_id 
JOIN course_videos cv ON c.id = cv.course_id 
JOIN videos v ON v.id = cv.video_id 
JOIN categories ct ON ct.id = c.category_id WHERE u.id = 1;

